	     *     event.tagReference(); // ["e", "eventid"]
	     * @returns {NDKTag} The NDKTag object referencing this event
	     */
	    tagReference(): NDKTag {
	        // NIP-33
	        if (this.isParamReplaceable()) {
            return ["a", this.tagId()];
	        }
	        return ["e", this.tagId()];
	    }
	    /**
	     * Provides the filter that will return matching events for this event.
	     *
	     * @example
	     *    event = new NDKEvent(ndk, { kind: 30000, pubkey: 'pubkey', tags: [ ["d", "d-code"] ] });
	     *    event.filter(); // { "#a": ["30000:pubkey:d-code"] }
     * @example
	     *    event = new NDKEvent(ndk, { kind: 1, pubkey: 'pubkey', id: "eventid" });
	     *    event.filter(); // { "#e": ["eventid"] }
	     *
	     * @returns The filter that will return matching events for this event
	     */
	    filter(): NDKFilter {
	        if (this.isParamReplaceable()) {
	            return { "#a": [this.tagId()] };
	        } else {
            return { "#e": [this.tagId()] };
	        }
	    }
	    /**
	     * Create a zap request for an existing event
	     *
	     * @param amount The amount to zap in millisatoshis
	     * @param comment A comment to add to the zap request
	     * @param extraTags Extra tags to add to the zap request
	     */
    async zap(
	        amount: number,
	        comment?: string,
	        extraTags?: NDKTag[]
	    ): Promise<string | null> {
	        if (!this.ndk) throw new Error("No NDK instance found");
	        this.ndk.assertSigner();
	        const zap = new Zap({
	            ndk: this.ndk,
	            zappedEvent: this,
        });
	        const paymentRequest = await zap.createZapRequest(
	            amount,
	            comment,
	            extraTags
	        );
	        // await zap.publish(amount);
	        return paymentRequest;
	    }
	    /**
     * Generates a deletion event of the current event
	     *
	     * @param reason The reason for the deletion
	     * @returns The deletion event
	     */
	    async delete(reason?: string): Promise<NDKEvent> {
	        if (!this.ndk) throw new Error("No NDK instance found");
	        this.ndk.assertSigner();
	        const e = new NDKEvent(this.ndk, {
	            kind: NDKKind.EventDeletion,
            content: reason || "",
	        } as NostrEvent);
	        e.tag(this);
	        await e.publish();
	        return e;
	    }
	    /**
	     * NIP-18 reposting event.
	     *
	     * @param publish Whether to publish the reposted event automatically
     * @param signer The signer to use for signing the reposted event
	     * @returns The reposted event
	     *
	     * @function
	     */
	    public repost = repost.bind(this);
	}

