#!/usr/bin/env node
	import { Command } from 'commander';
	// eslint-disable-next-line @typescript-eslint/ban-ts-comment
	// @ts-ignore
	import { version } from '../package.json';
	import { ConfigCommand } from './commands/config.command';
	import { LocalReviewCommand } from './commands/local-review.command';
	import { PullRequestReviewCommand } from './commands/pull-request-review.command';
	import { LocalReviewArgs } from './interfaces';
	import { CommitCommand } from './commands/commit.command';
import { checkForUpdate } from './utils/update-notifier';
	const program = new Command();
	program
	  .version(version || '0.0.0')
	  .description(
	    'revu-cli - Streamlining code reviews and commit message generation using GPT-4.',
	  );
	program
	  .command('config')
	  .description('Configure revu-cli')
  .action(async () => {
	    const configCommand = new ConfigCommand({ commandName: 'config' });
	    await configCommand.run();
	  });
	program
	  .command('pr <repository> <pull_request>')
	  .description('Review a pull request')
	  .action(async (repository: string, pullRequest: string) => {
	    const pullRequestReviewCommand = new PullRequestReviewCommand({
	      commandName: 'pr-review',
    });
	    await pullRequestReviewCommand.run({
	      fullRepository: repository,
	      pullRequest,
	    });
	  });
	program
	  .command('local')
	  .description('Review local changes or a specific file')
	  .option('-f, --filename <filename>', 'filename to review', '')
  .option('-d, --directory <directory>', 'directory of the file to review', '.')
	  .action(async (localReviewArgs: LocalReviewArgs) => {
	    const localReviewCommand = new LocalReviewCommand({
	      commandName: 'local-review',
	    });
	    await localReviewCommand.run(localReviewArgs);
	  });
	program
	  .command('commit')
	  .description('Generate commit message and commit selected files')
  .action(async () => {
	    const commitCommand = new CommitCommand({
	      commandName: 'commit',
	    });
	    await commitCommand.run();
	  });
	program.parseAsync(process.argv).then(() => {
	  checkForUpdate(version);
	});
	if (!process.argv.slice(2).length) {
  program.outputHelp();
	}

import { createLogger, format, transports } from 'winston';
	export const logger = createLogger({
	  level: 'info',
	  transports: [
	    new transports.Console({
	      level: 'info',
	      format: format.combine(
	        format.colorize(),
	        format.printf((info) => {
	          return `${info.message}`;
        }),
	      ),
	    }),
	  ],
	});

import ora from 'ora';
	import { CommandConfig } from '../interfaces';
	import { logger } from '../logger';
	export abstract class BaseCommand<T> {
	  protected config: CommandConfig;
	  protected spinner: ora.Ora;
	  protected constructor(config: CommandConfig) {
	    this.config = config;
	    this.spinner = ora();
	  }
  protected abstract _run(args?: T): Promise<void>;
	  public async run(args?: T): Promise<void> {
	    try {
	      await this._run(args);
	    } catch (error: any) {
	      this.spinner.stop();
	      logger.error(error.message);
	      process.exit(1);
	    }
	  }
}

import { CommandConfig } from '../interfaces';
	import { ConfigService } from '../services/config.service';
	import { OpenAiService } from '../services/openai.service';
	import { GithubService } from '../services/git/github.service';
	import { logger } from '../logger';
	import { BaseCommand } from './base.command';
	interface PullRequestReviewArgs {
	  fullRepository: string;
	  pullRequest: string;
	}
export class PullRequestReviewCommand extends BaseCommand<PullRequestReviewArgs> {
	  constructor(config: CommandConfig) {
	    super(config);
	  }
	  protected async _run({
	    fullRepository,
	    pullRequest,
	  }: PullRequestReviewArgs): Promise<void> {
	    const config = ConfigService.load();
	    const openAIConfig = config.llm.openai;
    const pullRequestUrl = GithubService.getPullRequestUrl(
	      fullRepository,
	      pullRequest,
	    );
	    logger.info(`Reviewing ${pullRequestUrl}`);
	    const pullRequestDiff = await GithubService.getPRDiff(
	      config.github,
	      config.git,
	      fullRepository,
	      pullRequest,
    );
	    this.spinner.text = 'Reviewing...';
	    this.spinner.start();
	    const review = await OpenAiService.reviewDiff(
	      openAIConfig,
	      pullRequestDiff,
	    );
	    this.spinner.stop();
	    logger.info(review);
	  }
}

import {
	  CommandConfig,
	  GitConfig,
	  LocalReviewArgs,
	  OpenAIConfig,
	} from '../interfaces';
	import { ConfigService } from '../services/config.service';
	import { OpenAiService } from '../services/openai.service';
	import { GitLocalService } from '../services/git/git-local.service';
	import { FileService } from '../services/file.service';
import { logger } from '../logger';
	import { BaseCommand } from './base.command';
	export class LocalReviewCommand extends BaseCommand<LocalReviewArgs> {
	  constructor(config: CommandConfig) {
	    super(config);
	  }
	  private async reviewDiff(
	    openAIConfig: OpenAIConfig,
	    gitConfig: GitConfig,
	  ): Promise<string> {
    const localDiff = await GitLocalService.getLocalDiff({
	      ignorePatterns: gitConfig.ignorePatterns,
	    });
	    logger.info('Reviewing local changes');
	    this.spinner.start();
	    return OpenAiService.reviewDiff(openAIConfig, localDiff);
	  }
	  private async reviewFile(
	    openAIConfig: OpenAIConfig,
	    directory: string,
    filename: string,
	  ): Promise<string> {
	    const getFileResponse = await FileService.getFileContentAndName(
	      directory,
	      filename,
	    );
	    const contentWithLineNumbers = FileService.addLineNumbers(
	      getFileResponse.content,
	    );
	    logger.info(`Reviewing ${getFileResponse.filename}`);
    this.spinner.start();
	    return OpenAiService.reviewFile(
	      openAIConfig,
	      contentWithLineNumbers,
	      getFileResponse.filename,
	    );
	  }
	  protected async _run({
	    directory,
	    filename,
  }: LocalReviewArgs): Promise<void> {
	    const config = ConfigService.load();
	    const openAIConfig = config.llm.openai;
	    const gitConfig = config.git;
	    this.spinner.text = 'Reviewing...';
	    const review = filename
	      ? await this.reviewFile(openAIConfig, directory, filename)
	      : await this.reviewDiff(openAIConfig, gitConfig);
	    this.spinner.stop();
	    logger.info(review);
  }
	}

import prompts from 'prompts';
	import { CommandConfig } from '../interfaces';
	import { ConfigService } from '../services/config.service';
	import { BaseCommand } from './base.command';
	class ConfigCommandError extends Error {
	  constructor(message: string) {
	    super(message);
	    this.name = 'ConfigCommandError';
	  }
	}
export class ConfigCommand extends BaseCommand<void> {
	  constructor(config: CommandConfig) {
	    super(config);
	  }
	  protected async _run(): Promise<void> {
	    const response = await prompts(
	      [
	        {
	          type: 'password',
	          name: 'githubToken',
          message: 'Please enter your GitHub token:',
	          validate: (input: string) => {
	            if (input.length === 0) {
	              return 'GitHub token cannot be empty!';
	            }
	            return true;
	          },
	        },
	        {
	          type: 'password',
          name: 'openApiKey',
	          message: 'Please enter your OpenAI API key:',
	          validate: (input: string) => {
	            if (input.length === 0) {
	              return 'OpenAI API key cannot be empty!';
	            }
	            return true;
	          },
	        },
	      ],
      {
	        onCancel: () => {
	          throw new ConfigCommandError('Setup was cancelled by the user');
	        },
	      },
	    );
	    await ConfigService.save({
	      githubToken: response.githubToken,
	      openaiApiKey: response.openApiKey,
	    });
  }
	}

import prompts from 'prompts';
	import {
	  CommandConfig,
	  CommitAction,
	  FileSelectionStatus,
	  GitConfig,
	  GitDiff,
	  LocalReviewArgs,
	} from '../interfaces';
	import { ConfigService } from '../services/config.service';
import { GitLocalService } from '../services/git/git-local.service';
	import { FileService } from '../services/file.service';
	import { logger } from '../logger';
	import { OpenAiService } from '../services/openai.service';
	import { BaseCommand } from './base.command';
	class CommitCommandError extends Error {
	  constructor(message: string) {
	    super(message);
	    this.name = 'CommitCommandError';
	  }
}
	export class CommitCommand extends BaseCommand<LocalReviewArgs> {
	  constructor(config: CommandConfig) {
	    super(config);
	  }
	  private async filesDiff(
	    filenames: string[],
	    gitConfig: GitConfig,
	  ): Promise<GitDiff> {
	    logger.info('Reviewing local changes for commit');
    return GitLocalService.getFilesDiff(filenames, {
	      ignorePatterns: gitConfig.ignorePatterns,
	    });
	  }
	  private async selectChangedFiles(): Promise<FileSelectionStatus> {
	    const fileChanges = await GitLocalService.getFilesChanged();
	    const selectedFiles = await FileService.selectFiles(fileChanges);
	    const selectedFileNames = new Set(
	      selectedFiles.map((file) => file.filename),
	    );
    const allFileNames = fileChanges.map((fileChange) => fileChange.filename);
	    const unselectedFileNames = allFileNames.filter(
	      (filename) => !selectedFileNames.has(filename),
	    );
	    return {
	      selectedFileNames: Array.from(selectedFileNames),
	      unselectedFileNames: unselectedFileNames,
	    };
	  }
	  private async promptShouldContinueCommit(): Promise<boolean> {
    const response = await prompts({
	      type: 'confirm',
	      name: 'value',
	      message: 'Do you want to continue commit?',
	      initial: false,
	    });
	    return response.value;
	  }
	  private async getCommitAction(): Promise<CommitAction> {
	    const response = await prompts({
      type: 'select',
	      name: 'value',
	      message: 'Do you want to commit the message, replace it, or do nothing?',
	      choices: [
	        { title: 'Commit', value: CommitAction.COMMIT },
	        { title: 'Replace', value: CommitAction.REPLACE },
	        { title: 'Do Nothing', value: CommitAction.SKIP },
	      ],
	      initial: 0,
	    });
    if (!response.value) {
	      throw new CommitCommandError('Commit action is required');
	    }
	    return response.value;
	  }
	  private async promptReplaceCommitMessage(
	    initialMessage: string,
	  ): Promise<string> {
	    const response = await prompts({
	      type: 'text',
      name: 'value',
	      message: 'Enter the new commit message:',
	      initial: initialMessage,
	    });
	    if (!response.value) {
	      throw new CommitCommandError('Commit message is required');
	    }
	    return response.value;
	  }
	  protected async _run(): Promise<void> {
    let shouldContinueCommit = true;
	    const config = ConfigService.load();
	    const gitConfig = config.git;
	    const openAIConfig = config.llm.openai;
	    while (shouldContinueCommit) {
	      const { selectedFileNames, unselectedFileNames } =
	        await this.selectChangedFiles();
	      const diff = await this.filesDiff(selectedFileNames, gitConfig);
	      logger.info('Generating commit message');
	      const commitHistory = await GitLocalService.getCommitHistory(
        gitConfig.maxCommitHistory,
	      );
	      this.spinner.text = 'Generating commit message...';
	      this.spinner.start();
	      const commitMessage = await OpenAiService.generateCommitMessage(
	        openAIConfig,
	        diff,
	        commitHistory,
	      );
	      this.spinner.stop();
      logger.info(commitMessage);
	      const commitAction = await this.getCommitAction();
	      shouldContinueCommit = commitAction !== CommitAction.SKIP;
	      if (commitAction !== CommitAction.SKIP) {
	        const messageToCommit =
	          commitAction === CommitAction.COMMIT
	            ? commitMessage
	            : await this.promptReplaceCommitMessage(commitMessage);
	        await GitLocalService.commit(messageToCommit, selectedFileNames);
	        shouldContinueCommit =
          unselectedFileNames.length === 0
	            ? false
	            : await this.promptShouldContinueCommit();
	      }
	    }
	  }
	}

import { execSync } from 'child_process';
	import chalk from 'chalk';
	import { logger } from '../logger';
	export const checkForUpdate = (version: string) => {
	  try {
	    const latestVersion = execSync('npm show revu-cli version')
	      .toString()
	      .trim();
	    if (latestVersion !== version) {
	      logger.info(
        chalk.yellow(
	          `\nNew version (${latestVersion}) of revu-cli is available. Consider updating.`,
	        ),
	      );
	    }
	  } catch (error) {
	    logger.error(chalk.red(`\nFailed to check for updates.`));
	  }
	};

