export function accessTokenFromCookies(cookies: string[]): string | undefined {
	  for (const cookie of cookies) {
	    // Matches everything between accessToken= and ;
	    const matches = cookie.match(/accessToken=([^;]+)/);
	    if (matches) {
	      // The first match includes "accessToken=", so we want the second match.
	      return matches[1];
	    }
	  }
	}
export function xsrfTokenFromCookies(cookies: string[]): string | undefined {
	  for (const cookie of cookies) {
	    // Matches everything between xsrfToken= and ;
	    const matches = cookie.match(/xsrfToken=([^;]+)/);
	    if (matches) {
	      // The first match includes "xsrfToken=", so we want the second match.
	      return matches[1];
	    }
	  }
	}

import {
	  WorkflowTemplateType,
	  workflowTemplate,
	} from "../resources/workflowTemplate";
	import { getCredentials } from "../utils/credentials";
	/*
	 * CAUTION: Puppeteer import takes ~90ms. Prefer to dynamically import this file.
	 */
	const puppeteer = require("puppeteer");
	declare global {
  interface Window {
	    generateWorkflowFromTemplateData: any;
	  }
	}
	// https://stackoverflow.com/a/61304202
	const waitTillHTMLRendered = async (page: any, timeout = 30000) => {
	  const checkDurationMsecs = 1000;
	  const maxChecks = timeout / checkDurationMsecs;
	  let lastHTMLSize = 0;
	  let checkCounts = 1;
  let countStableSizeIterations = 0;
	  const minStableSizeIterations = 2;
	  while (checkCounts++ <= maxChecks) {
	    const html = await page.content();
	    const currentHTMLSize = html.length;
	    // Uncomment for debugging
	    // const bodyHTMLSize = await page.evaluate(
	    //   () => document.body.innerHTML.length
	    // );
	    // console.log(
    //   "last: ",
	    //   lastHTMLSize,
	    //   " <> curr: ",
	    //   currentHTMLSize,
	    //   " body html size: ",
	    //   bodyHTMLSize
	    // );
	    if (lastHTMLSize != 0 && currentHTMLSize == lastHTMLSize)
	      countStableSizeIterations++;
	    else countStableSizeIterations = 0; //reset the counter
    // Page rendered fully
	    if (countStableSizeIterations >= minStableSizeIterations) {
	      break;
	    }
	    lastHTMLSize = currentHTMLSize;
	    await page.waitForTimeout(checkDurationMsecs);
	  }
	};
	export const generateWorkflowMetadata = async (tableName: string) => {
	  const credentials = getCredentials();
  if (!credentials) {
	    return;
	  }
	  try {
	    // Launch Puppeteer and navigate to subdomain.retool.com/workflows
	    const browser = await puppeteer.launch({
	      headless: "new",
	      // Uncomment this line to see the browser in action
	      // headless: false,
	    });
    const page = await browser.newPage();
	    const domain = new URL(credentials.origin).hostname;
	    const cookies = [
	      {
	        domain,
	        name: "accessToken",
	        value: credentials.accessToken,
	      },
	      {
	        domain,
        name: "xsrfToken",
	        value: credentials.xsrf,
	      },
	    ];
	    await page.setCookie(...cookies);
	    await page.goto(`${credentials.origin}/workflows`);
	    await waitTillHTMLRendered(page);
	    // Call window.generateWorkflowFromTemplateData() on the page
	    const generatedWorkflowMetadata = await page.evaluate(
	      (
        tableName: string,
	        workflowTemplate: WorkflowTemplateType,
	        retoolDBUuid: string
	      ) => {
	        // Replaces instances of "name_placeholder" with the newly created table name
	        workflowTemplate.map((item) => {
	          if (item.pluginTemplate.template.tableName === "name_placeholder") {
	            item.pluginTemplate.template.tableName = `\"${tableName}\"`;
	          }
	          if (item.pluginTemplate.template.query.includes("name_placeholder")) {
            item.pluginTemplate.template.query =
	              item.pluginTemplate.template.query.replace(
	                "name_placeholder",
	                `\"${tableName}\"`
	              );
	          }
	          // Inject retool DB UUID
	          if (
	            item.block.pluginId === "createQuery" ||
	            item.block.pluginId === "readQuery" ||
            item.block.pluginId === "updateQuery" ||
	            item.block.pluginId === "destroyQuery"
	          ) {
	            item.block.resourceName = retoolDBUuid;
	            item.pluginTemplate.resourceName = retoolDBUuid;
	          }
	        });
	        const payload = {
	          name: `${tableName} CRUD Workflow`,
	          templateData: workflowTemplate,
          resources: [],
	        };
	        const workflow = window.generateWorkflowFromTemplateData(payload);
	        return workflow;
	      },
	      tableName,
	      workflowTemplate,
	      credentials.retoolDBUuid
	    );
	    await browser.close();
    return generatedWorkflowMetadata;
	  } catch (error) {
	    console.error("Error:", error);
	  }
	};

import { Entry } from "@napi-rs/keyring";
	import { getRequest } from "./networking";
	import { isAccessTokenValid, isOriginValid, isXsrfValid } from "./validation";
	const axios = require("axios");
	const inquirer = require("inquirer");
	const ora = require("ora");
	const RETOOL_CLI_SERVICE_NAME = "Retool CLI";
	const RETOOL_CLI_ACCOUNT_NAME = "retool-cli-user";
	/*
	 * Credential management using keyring-rs. This is a cross-platform library
 * which uses the OS's native credential manager.
	 * https://github.com/Brooooooklyn/keyring-node
	 * https://github.com/hwchen/keyring-rs
	 */
	export type Credentials = {
	  origin: string; // The 3 required properties are fetched during login.
	  xsrf: string;
	  accessToken: string;
	  gridId?: string; // The next 3 properties are fetched the first time user interacts with RetoolDB.
	  retoolDBUuid?: string;
  hasConnectionString?: boolean;
	  firstName?: string; // The next 3 properties are sometimes fetched during login.
	  lastName?: string;
	  email?: string;
	  telemetryEnabled: boolean; // The next 2 properties control telemetry.
	  telemetryLastSent?: number;
	};
	// Legacy way of getting credentials.
	export async function askForCookies() {
	  let { origin } = await inquirer.prompt([
    {
	      name: "origin",
	      message: "What is your Retool origin? (e.g., https://my-org.retool.com).",
	      type: "input",
	    },
	  ]);
	  //Check if last character is a slash. If so, remove it.
	  if (origin[origin.length - 1] === "/") {
	    origin = origin.slice(0, -1);
	  }
  if (!isOriginValid(origin)) {
	    console.log("Error: Origin is invalid. Remember to include https://.");
	    process.exit(1);
	  }
	  const { xsrf } = await inquirer.prompt([
	    {
	      name: "xsrf",
	      message:
	        "What is your XSRF token? (e.g., 26725f72-8129-47f7-835a-cba0e5dbcfe6) \n  Log into Retool, open cookies inspector.\n  In Chrome, hit ‚åò+‚å•+I (Mac) or Ctrl+Shift+I (Windows, Linux) to open dev tools.\n  Application tab > your-org.retool.com in Cookies menu > double click cookie value and copy it.",
	      type: "input",
    },
	  ]);
	  if (!isXsrfValid(xsrf)) {
	    console.log("Error: XSRF token is invalid.");
	    process.exit(1);
	  }
	  const { accessToken } = await inquirer.prompt([
	    {
	      name: "accessToken",
	      message: `What is your access token? It's also found in the cookies inspector.`,
      type: "input",
	    },
	  ]);
	  if (!isAccessTokenValid(accessToken)) {
	    console.log("Error: Access token is invalid.");
	    process.exit(1);
	  }
	  persistCredentials({
	    origin,
	    xsrf,
    accessToken,
	    telemetryEnabled: true,
	  });
	  console.log("Successfully saved credentials.");
	}
	export function persistCredentials(credentials: Credentials) {
	  const entry = new Entry(RETOOL_CLI_SERVICE_NAME, RETOOL_CLI_ACCOUNT_NAME);
	  entry.setPassword(JSON.stringify(credentials));
	}
	export function getCredentials(): Credentials | undefined {
  const entry = new Entry(RETOOL_CLI_SERVICE_NAME, RETOOL_CLI_ACCOUNT_NAME);
	  const password = entry.getPassword();
	  if (password) {
	    return JSON.parse(password);
	  }
	}
	export function doCredentialsExist(): boolean {
	  const entry = new Entry(RETOOL_CLI_SERVICE_NAME, RETOOL_CLI_ACCOUNT_NAME);
	  const password = entry.getPassword();
	  if (password) {
    return true;
	  }
	  return false;
	}
	export function deleteCredentials() {
	  const entry = new Entry(RETOOL_CLI_SERVICE_NAME, RETOOL_CLI_ACCOUNT_NAME);
	  entry.deletePassword();
	}
	// Fetch gridId and retoolDBUuid from Retool. Persist to keychain.
	async function fetchDBCredentials() {
  const credentials = getCredentials();
	  if (!credentials) {
	    return;
	  }
	  // 1. Fetch all resources
	  const resources = await getRequest(`${credentials.origin}/api/resources`);
	  // 2. Filter down to Retool DB UUID
	  const retoolDBs = resources?.data?.resources?.filter(
	    (resource: any) => resource.displayName === "retool_db"
	  );
  if (retoolDBs?.length < 1) {
	    console.log(
	      `\nError: Retool DB not found. Create one at ${credentials.origin}/resources`
	    );
	    return;
	  }
	  const retoolDBUuid = retoolDBs[0].name;
	  // 3. Fetch Grid Info
	  const grid = await getRequest(
	    `${credentials.origin}/api/grid/retooldb/${retoolDBUuid}?env=production`
  );
	  persistCredentials({
	    ...credentials,
	    retoolDBUuid,
	    gridId: grid?.data?.gridInfo?.id,
	    hasConnectionString: grid?.data?.gridInfo?.connectionString?.length > 0,
	  });
	}
	export async function getAndVerifyCredentialsWithRetoolDB() {
	  const spinner = ora("Verifying Retool DB credentials").start();
  let credentials = getCredentials();
	  if (!credentials) {
	    spinner.stop();
	    console.log(
	      `Error: No credentials found. To log in, run: \`retool login\``
	    );
	    process.exit(1);
	  }
	  axios.defaults.headers["x-xsrf-token"] = credentials.xsrf;
	  axios.defaults.headers.cookie = `accessToken=${credentials.accessToken};`;
  if (!credentials.gridId || !credentials.retoolDBUuid) {
	    await fetchDBCredentials();
	    credentials = getCredentials();
	    if (!credentials?.gridId || !credentials?.retoolDBUuid) {
	      spinner.stop();
	      console.log(`Error: No Retool DB credentials found.`);
	      process.exit(1);
	    }
	  }
	  spinner.stop();
  return credentials;
	}
	export function getAndVerifyCredentials() {
	  const spinner = ora("Verifying Retool credentials").start();
	  const credentials = getCredentials();
	  if (!credentials) {
	    spinner.stop();
	    console.log(
	      `Error: No credentials found. To log in, run: \`retool login\``
	    );
    process.exit(1);
	  }
	  axios.defaults.headers["x-xsrf-token"] = credentials.xsrf;
	  axios.defaults.headers.cookie = `accessToken=${credentials.accessToken};`;
	  spinner.stop();
	  return credentials;
	}

