import * as fs from "fs";
	import * as path from "path";
	import * as tar from "tar";
	const axios = require("axios");
	const inquirer = require("inquirer");
	// url should be a tarfile link like https://api.github.com/repos/tryretool/retool-examples/tarball/main
	// subfolderPath should be a path to a subfolder within the tarfile, e.g. hello_world/typescript
	// destinationPath should be a path to a folder where the contents of the github subfolder will be extracted
	export async function downloadGithubSubfolder(
	  githubUrl: string,
  subfolderPath: string,
	  destinationPath: string
	) {
	  try {
	    const url = `${githubUrl}?path=${subfolderPath}`;
	    const response = await axios.get(url, { responseType: "arraybuffer" });
	    // Delete the directory if it already exists
	    if (fs.existsSync(destinationPath)) {
	      const { proceedWithDirectoryCreation } = (await inquirer.prompt([
	        {
          name: "proceedWithDirectoryCreation",
	          message:
	            "It looks like this directory already exists, can we delete it and continue? (Y/N)",
	          type: "input",
	        },
	      ])) as { proceedWithDirectoryCreation: string };
	      if (proceedWithDirectoryCreation.toLowerCase() !== "y") {
	        console.log("Aborting...");
	        process.exit(1);
	      }
      await fs.promises.rm(destinationPath, { recursive: true });
	    }
	    fs.mkdirSync(destinationPath, { recursive: true });
	    const tarballPath = path.join(destinationPath, "tarball.tar.gz");
	    fs.writeFileSync(tarballPath, response.data);
	    await tar.x({
	      file: tarballPath,
	      cwd: destinationPath,
	      strip: subfolderPath.split("/").length + 1, // remove the top-level directories
	    });
    fs.unlinkSync(tarballPath);
	  } catch (error: any) {
	    console.error("Error:", error.message);
	  }
	}
	export function saveEnvVariablesToFile(
	  envVariables: Record<string, string>,
	  filePath: string
	) {
	  try {
    const envContent = Object.entries(envVariables)
	      .map(([key, value]) => `${key}=${value}`)
	      .join("\n");
	    fs.writeFileSync(filePath, envContent);
	  } catch (error: any) {
	    console.error("Error saving environment variables:", error.message);
	  }
	}

import { Credentials, getCredentials, persistCredentials } from "./credentials";
	import { postRequest } from "./networking";
	// @ts-ignore
	import { version } from "../../package.json";
	export async function logDAU(
	  credentials?: Credentials,
	  persistCreds = true
	): Promise<boolean> {
	  credentials = credentials || getCredentials();
	  const twelveHours = 12 * 60 * 60 * 1000;
  // Don't send telemetry if user has opted out or if we've already sent telemetry in the last 12 hours.
	  if (
	    !credentials ||
	    credentials.telemetryEnabled !== true ||
	    (credentials.telemetryLastSent &&
	      Date.now() - credentials.telemetryLastSent < twelveHours)
	  ) {
	    return false;
	  }
	  const payload = {
    "CLI Version": version,
	    email: credentials.email,
	    origin: credentials.origin,
	    os: process.platform,
	  };
	  // Send a POST request to Retool's telemetry endpoint.
	  const res = await postRequest(`https://p.retool.com/v2/p`, {
	    event: "CLI DAU",
	    properties: payload,
	  });
  if (res.status === 200) {
	    // Update the last time we sent telemetry.
	    credentials.telemetryLastSent = Date.now();
	    if (persistCreds) {
	      persistCredentials(credentials);
	    }
	    return true;
	  }
	  return false;
	}

/* eslint-disable */
	const emailRegex =
	  /^[-!#$%&'*+\/0-9=?A-Z^_a-z{|}~](\.?[-!#$%&'*+\/0-9=?A-Z^_a-z`{|}~])*@[a-zA-Z0-9](-*\.?[a-zA-Z0-9])*\.[a-zA-Z](-?[a-zA-Z0-9])+$/;
	/* eslint-enable */
	//https://stackoverflow.com/questions/52456065/how-to-format-and-validate-email-node-js
	export function isEmailValid(email: string) {
	  if (!email) return false;
	  if (email.length > 254) return false;
	  if (!emailRegex.test(email)) return false;
	  // Further checking of some things regex can't handle
  const parts = email.split("@");
	  if (parts[0].length > 64) return false;
	  const domainParts = parts[1].split(".");
	  if (
	    domainParts.some(function (part) {
	      return part.length > 63;
	    })
	  )
	    return false;
	  return true;
}
	export function isOriginValid(origin: string) {
	  // https://www.regextester.com/23
	  const hostnameRegEx =
	    /^(https?:\/\/)(([a-zA-Z0-9]|[a-zA-Z0-9][a-zA-Z0-9\-]*[a-zA-Z0-9])\.)*([A-Za-z0-9]|[A-Za-z0-9][A-Za-z0-9\-]*[A-Za-z0-9])$/;
	  if (origin.match(hostnameRegEx)) {
	    return true;
	  }
	  return false;
	}
export function isXsrfValid(xsrf: string) {
	  // https://stackoverflow.com/questions/7905929/how-to-test-valid-uuid-guid
	  const uuidRegEx =
	    /^[0-9a-f]{8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/;
	  if (xsrf.match(uuidRegEx)) {
	    return true;
	  }
	  return false;
	}
	export function isAccessTokenValid(accessToken: string) {
  // https://stackoverflow.com/questions/61802832/regex-to-match-jwt
	  const jwtRegEx = /^[\w-]+\.[\w-]+\.[\w-]+$/;
	  if (accessToken.match(jwtRegEx)) {
	    return true;
	  }
	  return false;
	}

