import { Credentials } from "./credentials";
	import { getRequest, postRequest } from "./networking";
	export type ResourceByEnv = Record<string, Resource>;
	export type Resource = {
	  displayName: string;
	  id: number;
	  name: string;
	  type: string;
	  environment: string;
	  environmentId: string;
  uuid: string;
	  organizationId: number;
	  resourceFolderId: number;
	};
	export async function getResourceByName(
	  resourceName: string,
	  credentials: Credentials
	): Promise<ResourceByEnv> {
	  const getResourceResult = await getRequest(
	    `${credentials.origin}/api/resources/names/${resourceName}`
  );
	  const { resourceByEnv } = getResourceResult.data;
	  if (!resourceByEnv) {
	    console.log("Error finding resource by that id.");
	    console.log(getResourceResult.data);
	    process.exit(1);
	  } else {
	    return resourceByEnv;
	  }
	}
export async function createResource({
	  resourceType,
	  credentials,
	  displayName,
	  resourceFolderId,
	  resourceOptions,
	}: {
	  resourceType: string;
	  credentials: Credentials;
	  displayName?: string;
  resourceFolderId?: number;
	  resourceOptions?: Record<string, any>;
	}): Promise<Resource> {
	  const createResourceResult = await postRequest(
	    `${credentials.origin}/api/resources/`,
	    {
	      type: resourceType,
	      displayName,
	      resourceFolderId,
	      options: resourceOptions ? resourceOptions : {},
    },
	    false,
	    {},
	    false
	  );
	  const resource = createResourceResult.data;
	  if (!resource) {
	    throw new Error("Error creating resource.");
	  } else {
	    return resource;
  }
	}

const axios = require("axios");
	// Convenience function for making network requests. Error handling is centralized here.
	// If nothing is returned, the request failed and the process may exit.
	export async function postRequest(
	  url: string,
	  body: any,
	  exitOnFailure = true,
	  headers = {},
	  shouldHandleError = true
	) {
  if (!shouldHandleError) {
	    return await axios.post(
	      url,
	      {
	        ...body,
	      },
	      {
	        headers: {
	          ...headers,
	        },
      }
	    );
	  } else {
	    try {
	      const response = await axios.post(
	        url,
	        {
	          ...body,
	        },
	        {
          headers: {
	            ...headers,
	          },
	        }
	      );
	      return response;
	    } catch (error: any) {
	      handleError(error, exitOnFailure, url);
	    }
	  }
}
	export async function getRequest(url: string, exitOnFailure = true) {
	  try {
	    const response = await axios.get(url);
	    return response;
	  } catch (error: any) {
	    handleError(error, exitOnFailure, url);
	  }
	}
	export async function deleteRequest(url: string, exitOnFailure = true) {
  try {
	    const response = await axios.delete(url);
	    return response;
	  } catch (error: any) {
	    handleError(error, exitOnFailure, url);
	  }
	}
	function handleError(error: any, exitOnFailure = true, url: string) {
	  if (error.response) {
	    // The request was made, but the server responded with a status code outside the 2xx range
    console.error("\n\nHTTP Request Error:", error.response.data);
	  } else {
	    console.error("\n\nNetwork error:", error.toJSON());
	  }
	  if (process.env.DEBUG) {
	    console.error(error);
	  }
	  console.error(`\nFailed to make request to ${url}.`);
	  if (exitOnFailure) {
	    process.exit(1);
  }
	}

import { ConnectionStringParser } from "connection-string-parser";
	import { getCredentials } from "./credentials";
	import { getRequest } from "./networking";
	const chalk = require("chalk");
	// Print a psql command to connect to the Retool DB.
	// Connnection string is never persisted, it's fetched when needed.
	// This is done to avoid storing the password in plaintext.
	export async function logConnectionStringDetails() {
	  const connectionString = await getConnectionString();
	  if (connectionString) {
    const parsed = new ConnectionStringParser({
	      scheme: "postgresql",
	      hosts: [],
	    }).parse(connectionString);
	    console.log(
	      `${chalk.bold("Connect via psql:")} PGPASSWORD=${
	        parsed.password
	      } psql -h ${parsed.hosts[0].host} -U ${parsed.username} ${
	        parsed.endpoint
	      }`
    );
	    console.log(
	      `${chalk.bold("Postgres Connection URL:")} ${connectionString}`
	    );
	  }
	}
	async function getConnectionString(): Promise<string | undefined> {
	  const credentials = getCredentials();
	  if (
	    !credentials ||
    !credentials.retoolDBUuid ||
	    !credentials.hasConnectionString
	  ) {
	    return;
	  }
	  const grid = await getRequest(
	    `${credentials.origin}/api/grid/retooldb/${credentials.retoolDBUuid}?env=production`,
	    false
	  );
	  return grid.data?.gridInfo?.connectionString;
}
