import { CommandInteraction, Client, Interaction } from 'discord.js'
	import Commands from '../Commands'
	import safeExecute from '../utils/commands'
	// Slash command validation
	const handleSlashCommand = async (
	  client: Client,
	  interaction: CommandInteraction,
	): Promise<void> => {
	  const slashCommand = Commands.find((c) => c.data.name === interaction.commandName)
	  if (!slashCommand) {
    interaction.followUp({ content: 'Hm, I do not understand this command.' })
	    return
	  }
	  await interaction.deferReply()
	  safeExecute(slashCommand, client, interaction)
	}
	// Check if the message is a bot command
	export default (client: Client): void => {
	  client.on('interactionCreate', async (interaction: Interaction) => {
	    if (interaction.isCommand() || interaction.isContextMenuCommand()) {
      await handleSlashCommand(client, interaction)
	    }
	  })
	}

// Get environment variables and enforces they aren't missing
	export default (key: string): string => {
	  const param = process.env[key]
	  if (!param || param === '' || param === '0') {
	    throw new Error(
	      `${key} is missing from your env file or is invalid.`,
	    )
	  }
	  return param
	}

import { Client, CommandInteraction } from 'discord.js'
	import { Command } from '../types/Command'
	export default async function safeExecute(
	  command: Command,
	  client: Client,
	  interaction: CommandInteraction,
	) {
	  try {
	    await command.execute(client, interaction)
	  } catch (error) {
    console.error(`Error while executing command ${command.data.name}:`, error)
	    await interaction.followUp({
	      content:
	       ':pensive: *I am terribly sorry, but it seems I\'ve run into some trouble:*\n'
	       + `:red_circle: **${error}**`,
	      ephemeral: true,
	    })
	  }
	}

import {
	  ChatInputCommandInteraction,
	  Client,
	  Collection,
	  InteractionReplyOptions,
	  Message,
	  TextBasedChannel,
	  ThreadChannel,
	} from 'discord.js'
	// Regex for discord messages
// e.g. https://discord.com/channels/1095842976/10969358/11023650
	const messageUrlRegex = /https:\/\/discord\.com\/channels\/(\d+)\/(\d+)\/(\d+)/
	// Will get the message from a discord message URL
	export async function getMessageFromURL(client: Client, url: string | null) {
	  if (!url) throw new Error("Message URL can't be undefined")
	  const matches = url.match(messageUrlRegex)
	  if (!matches) {
	    throw new Error('Invalid message URL. Expected discord/guild_id/channel_id/message_id')
	  }
	  const [, guildId, channelId, messageId] = matches
  const guild = await client.guilds.cache.get(guildId)
	  if (!guild) throw new Error('Server not found')
	  const channel = await guild.channels.cache.get(channelId)
	  if (!channel) throw new Error('Channel not found')
	  if (!channel.isTextBased()) throw new Error('Channel is not text based')
	  const message = await channel.messages.fetch(messageId)
	  if (!message) throw new Error('Message not found')
	  return message
	}
	// Replace all discord message URLs with their actual message content
export async function replaceMessageUrls(client: Client, conversation: string): Promise<string> {
	  const replacer = async (match: string) => {
	    const message = await getMessageFromURL(client, match)
	    return message.content
	  }
	  // Handle all message fetching in parallel
	  async function replaceAsync(str: string, regex: RegExp, asyncFn: Function): Promise<string> {
	    const globalRegex = new RegExp(regex.source, `${regex.flags}g`)
	    const matches = Array.from(str.matchAll(globalRegex))
	    const replacements = await Promise.all(matches.map((match) => asyncFn(match[0])))
    return matches.reduce((result, match, i) => result.replace(match[0], `(((reference to an other message: ${replacements[i]})))`), str)
	  }
	  // Return conversation with replaced URLs
	  const replacedConversation = await replaceAsync(conversation, messageUrlRegex, replacer)
	  return replacedConversation
	}
	// To mention users in reply messages
	export function mentionUser(userID: string) {
	  return `<@${userID}>`
	}
// Logic to simplify whether to use a reply or followup message
	export async function replyOrFollowup(
	  interaction: ChatInputCommandInteraction,
	  isReply: boolean,
	  reply: InteractionReplyOptions,
	  thread?: ThreadChannel,
	) {
	  // If in a thread, always just send message
	  if (thread) {
	    thread.send(reply.content!)
  } else {
	    (isReply ? interaction.deferReply : interaction.followUp).bind(interaction)(reply)
	  }
	}
	export async function waitForUserResponse(
	  userID: string, // User ID
	  max: number, // Maximum number of responses
	  time: number, // How long Alfred waits for
	  channel: TextBasedChannel,
	  thread?: ThreadChannel,
): Promise<Collection<string, Message>> {
	  const filter = (m: any) => m.author.id === userID
	  // Custom timeout promise to be used when the thread is not defined, so it doesn't blow up
	  const timeoutPromise = new Promise<Collection<string, Message>>(
	    // eslint-disable-next-line no-promise-executor-return
	    (resolve) => setTimeout(() => resolve(new Collection()), time + 1000),
	  )
	  // Listeners
	  const threadPromise = thread ? thread.awaitMessages({ filter, max, time }) : timeoutPromise
	  const channelPromise = channel.awaitMessages({ filter, max, time })
  // Listen to both channel and thread in parallel and return the first reply in either
	  return Promise.race([channelPromise, threadPromise])
	}

import { Collection, Message } from 'discord.js'
	export function addConversation(msg: Message) {
	  const attachments = msg.attachments.map((att) => att.url)
	  return `${msg.author.username}: ${msg.content} `
	    + `${attachments.length > 0 ? `[ATTACHMENTS: ${attachments}]` : ''}]\n`
	}
	export function stackConversation(
	  msgs: Collection<string, Message<true>> | Collection<string, Message<false>>,
	) {
	  let conversation = ''
  msgs.reverse().forEach((message: Message<true> | Message<false>) => {
	    conversation += addConversation(message)
	  })
	  return conversation
	}

import { createAppAuth } from '@octokit/auth-app'
	import { Octokit } from '@octokit/rest'
	import { AlfredGithubConfig } from '../types/AlfredGithubConfig'
	// Will create an octokit isntance based on Alfred's configuration
	export async function getOctokit(config: AlfredGithubConfig): Promise<Octokit> {
	  // Deconstruct config
	  const { installationId, ...options } = config
	  // Get auth for app
	  const auth = createAppAuth(options)
	  // Retrieve installation access token
  const installationAuthentication = await auth({
	    type: 'installation',
	    installationId,
	  })
	  // Return ocktokit for specific app installation
	  return new Octokit({
	    auth: installationAuthentication.token,
	  })
	}
	// Will create an issue in specified repository
export async function createIssue(
	  octokit: Octokit, // Octokit instance for that specific app installation
	  owner: string, // Owner of the repository
	  repo: string, // Name of the repository
	  title: string, // Issue title
	  body: string, // Content of the issue
	  labels?: string[], // Labels to assign to the issue
	): Promise<string> {
	  try {
	    const resp = await octokit.issues.create({
      owner,
	      repo,
	      title,
	      body,
	      labels,
	    })
	    return resp.data.html_url
	  } catch (error) {
	    console.error('Error creating issue:', error)
	    throw new Error(`Failed to create issue: ${error}`)
  }
	}
	// Will get the labels and their descriptions for a given repository
	export async function getRepositoryLabels(
	  octokit: Octokit, // Octokit instance for that specific app installation
	  owner: string, // Owner of the repository
	  repo: string, // Name of the repository
	): Promise<string> {
	  try {
	    const response = await octokit.issues.listLabelsForRepo({
      owner,
	      repo,
	      per_page: 200,
	    })
	    const labels = response.data
	    // Build a string with all labels
	    let labelsString = ''
	    labels.forEach((label) => {
	      labelsString += `${label.name}: ${label.description} \n`
	    })
    return labelsString
	  } catch (error) {
	    console.error("Error fetching repo's labels:", error)
	    throw new Error(`Error fetching repo's labels: ${error}`)
	  }
	}

