export function chartLine(
		data: IDataset,
		type: "money" | "percent",
		beginAtZero = true
	): IChartLine {
		return {
			type: "line",
			data,
			options: {
				interaction: {
				intersect: false,
				},
				scales: {
					y: {
						beginAtZero,
					},
				},
				plugins: {
					tooltip: {
						callbacks: {
						label: function (context: ITooltip) {
								let previousValue: number = null;
								let firstValue: number = null;
								let change = 0;
								let symbol: "+" | "-" = "+";
								let label = context.dataset.label || "";
								if (label) {
									label += ": ";
								}
								if (context.parsed.y !== null) {
								if (type === "money")
										label += new Intl.NumberFormat("en-US", {
											style: "currency",
											currency: "USD",
										}).format(context.parsed.y);
									else if (type === "percent")
										label += `${context.parsed.y}%`;
								}
								const currentValue: number = context.parsed.y;
								// NOTE: Skip
							if (isNaN(currentValue)) return label;
								// NOTE: Item index between 1 and the last one
								if (context.dataIndex - 1 >= 0) {
									// NOTE: Get Value from the previous point
									previousValue =
										context.dataset.data[context.dataIndex - 1];
									// NOTE: Skip
									if (previousValue === 0 || isNaN(previousValue))
										return label;
									// NOTE: Positive Change
								if (currentValue > previousValue) {
										symbol = "+";
										change =
											((currentValue - previousValue) /
												previousValue) *
											100;
									}
									// NOTE: Negative Change
									else {
										symbol = "-";
									change =
											((previousValue - currentValue) /
												previousValue) *
											100;
									}
									// NOTE: Append Label
									label += ` => ${
										change !== 0 ? symbol : ""
									}${change.toFixed(2)}%`;
								}
							// NOTE: Last item in the graph
								if (
									context.dataIndex ===
									context.dataset.data.length - 1
								) {
									firstValue = context.dataset.data[0];
									// NOTE: SKIP
									if (firstValue === 0 || isNaN(firstValue))
										return label;
									// NOTE: Positive Change
								if (currentValue > firstValue) {
										symbol = "+";
										change =
											((currentValue - firstValue) /
												firstValue) *
											100;
									}
									// NOTE: Negative Change
									else {
										symbol = "-";
									change =
											((firstValue - currentValue) /
												firstValue) *
											100;
									}
									// NOTE: Append Label
									label += ` > ${
										change !== 0 ? symbol : ""
									}${change.toFixed(2)}%`;
								}
							return label;
							},
						},
					},
				},
			},
		};
	}

interface IInput {
		type: string;
		id: string;
		value: number;
		timestamp: Date;
		extra: string;
	}
	interface IDataset {
		labels: string[];
		datasets: {
		label: string;
			borderColor: string;
			fill: boolean;
			tension: number;
			data: number[];
			segment: {
				borderColor?: (ctx: IContext) => string | Array<number> | undefined;
				borderDash: (ctx: IContext) => string | Array<number> | undefined;
			};
		}[];
}
	interface ITooltip {
		dataset: {
			label: string;
			data: number[];
		};
		parsed: {
			y: number;
		};
		dataIndex: number;
}
	interface IPluginSettings {
		models: {
			[key: string]: IModel;
		};
		colors: string[];
		debounce: string;
		csvSeparator: string;
	}
	interface IModel {
	dataSource: string;
		types: string[];
		output: string;
		beginAtZero: boolean;
		type: "money" | "percent";
	}
	interface IEvent {
		target: {
			innerHTML: string;
		};
}
	interface IChartLine {
		type: "line";
		data: IDataset;
		options: {
			interaction: {
				intersect: boolean;
			};
			scales: {
				y: {
				beginAtZero: boolean;
				};
			};
			plugins: {
				tooltip: {
					callbacks: {
						label: (context: ITooltip) => string;
					};
				};
			};
	};
	}
	interface IContext {
		p0: { skip: boolean };
		p1: { skip: boolean };
	}

import { MarkdownRenderChild } from "obsidian";
	import Chart, { ChartConfiguration } from "chart.js/auto";
	import { idToText } from "utils";
	export default class ChartRenderer extends MarkdownRenderChild {
		private data: IChartLine;
		private modelInfo: IModel;
		private model: string;
		private canvases: { [key: string]: HTMLCanvasElement } = {};
		constructor(
			modelInfo: IModel,
		data: IChartLine,
			model: string,
			el: HTMLElement
		) {
			super(el);
			this.modelInfo = modelInfo;
			this.data = data;
			this.model = model;
		}
		async onload() {
		if (!this.canvases[this.model]) {
				this.canvases[this.model] = document.createElement("canvas");
				// Chart
				new Chart(
					this.canvases[this.model].getContext("2d"),
					this.data as ChartConfiguration
				);
				this.containerEl.createEl("h3", { text: this.model });
				this.containerEl.append(this.canvases[this.model]);
				this.containerEl.createEl("p", {
				text: `Data Source: ${idToText(this.modelInfo.dataSource)}`,
				});
				this.containerEl.createEl("p", {
					text: `Output: ${idToText(this.modelInfo.output)}`,
				});
				this.containerEl.createEl("p", {
					text: `Found: ${this.data.data.datasets.reduce(
						(a, b) => (a += b.data.length),
						0
					)} data point(s)`,
			});
			} else {
				console.debug("Canvas already loaded");
			}
		}
	}

import FinDocPlugin from "main";
	import { Notice, TextFileView, WorkspaceLeaf, debounce } from "obsidian";
	import { getToday } from "utils";
	import { types } from "./constants";
	export const VIEW_TYPE_CSV = "csv-view";
	export class CSVView extends TextFileView {
		plugin: FinDocPlugin;
		tableData: string[];
		tableHeader: string;
		div: HTMLElement;
	parent: HTMLElement;
		constructor(leaf: WorkspaceLeaf, plugin: FinDocPlugin) {
			super(leaf);
			this.plugin = plugin;
		}
		getViewData() {
			return this.tableData.join("\n");
		}
		dropdown(selected = ""): HTMLElement {
			const id = new Date().getTime().toString();
		const dropdown = this.contentEl.createEl("select");
			dropdown.id = id;
			dropdown.setAttribute("value", selected);
			dropdown.onchange = () => {
				dropdown.setAttribute("value", dropdown.value);
			};
			types.forEach((option: string) => {
				const opt = this.contentEl.createEl("option");
				opt.value = option;
				opt.id = id + option.replace(" ", "_");
			opt.innerText = option;
				if (option === selected) opt.selected = true;
				dropdown.appendChild(opt);
			});
			return dropdown;
		}
		createTable(data: string[]) {
			this.div = this.contentEl.createDiv();
			const table = this.contentEl.createEl("table");
			//
		// Table Header
			//
			this.tableHeader = data[0];
			const headers = this.tableHeader.split(
				this.plugin.settings.csvSeparator
			);
			const trHeaders = this.contentEl.createEl("tr");
			headers.forEach((header) => {
				const th = this.contentEl.createEl("th");
				th.innerText = header;
			trHeaders.appendChild(th);
			});
			this.div.appendChild(trHeaders);
			//
			// Table Content
			//
			data.slice(1).forEach((line) => {
				const trContent = this.contentEl.createEl("tr");
				const lineData = line.split(this.plugin.settings.csvSeparator);
				lineData.push("ACTION");
			lineData.forEach((el, idx) => {
					const td = this.contentEl.createEl("td");
					td.id = `data_${idx}`;
					td.classList.add("findoc-line-data");
					if (idx === 0) {
						td.appendChild(this.dropdown(el));
					} else if (idx === lineData.length - 1) {
						td.appendChild(this.createBtnRemoveLine(trContent));
					} else {
						td.innerText = el;
					td.contentEditable = "true";
					}
					trContent.appendChild(td);
				});
				this.div.appendChild(trContent);
			});
			table.appendChild(this.div);
			this.parent.appendChild(table);
			this.createBtnAddLine();
		}
	createBtnRemoveLine(el: HTMLElement): HTMLElement {
			const btn = this.contentEl.createEl("button");
			btn.classList.add("findoc-btn-margin-top");
			btn.id = "deleteRow";
			btn.innerText = "Delete";
			btn.onClickEvent(() => {
				el.empty();
				this.saveData();
			});
			return btn;
	}
		createBtnAddLine() {
			const btn = this.contentEl.createEl("button");
			btn.classList.add("findoc-btn-margin-top");
			btn.id = "newRow";
			btn.innerText = "Add New Row";
			btn.onClickEvent(() => {
				const trContent = this.contentEl.createEl("tr");
				const lineData = [
					types[0],
				"ID",
					"0",
					getToday(),
					"EXTRA",
					"ACTION",
				];
				lineData.forEach((el, idx) => {
					const td = this.contentEl.createEl("td");
					td.classList.add("findoc-line-data");
					if (idx === 0) {
					td.appendChild(this.dropdown(el));
					} else if (idx === lineData.length - 1) {
						td.appendChild(this.createBtnRemoveLine(trContent));
					} else {
						td.innerText = el;
						td.contentEditable = "true";
					}
					trContent.appendChild(td);
				});
				this.div.appendChild(trContent);
		});
			this.parent.appendChild(btn);
		}
		setViewData(data: string, clear: boolean) {
			if (clear) this.clear();
			this.tableData = data.split("\n");
			this.parent = this.contentEl.createDiv();
			this.createTable(this.tableData);
			// Margin to avoid having the iphone keyboard hide last lines
			this.parent.classList.add("findoc-csv-parent");
		this.refresh();
		}
		refresh() {
			this.div.oninput = debounce(() => {
				this.saveData();
			}, parseInt(this.plugin.settings.debounce) || 1000);
		}
		saveData() {
			const rows = this.div.innerHTML.split(new RegExp(/<tr.*?>/));
			this.tableData = rows
			.map((column) =>
					column
						.split(new RegExp(/<td.*?>/))
						.slice(1)
						.filter((i) => !new RegExp(/<button.*?>.*<.*?>/).test(i))
						.map((i, idx) => {
							if (idx === 0) {
								// Select (Dropdown)
								return (
									i.split('value="')[1].split('"')[0] || types[0]
							);
							} else {
								// Input field
								return i
									.replaceAll(/<.*?>/g, "")
									.replaceAll(
										'&lt;br class="Apple-interchange-newline"&gt',
										""
									);
							}
					})
						.join(this.plugin.settings.csvSeparator)
				)
				// Clear empty lines
				.filter((r) => r.length !== 0);
			this.tableData = [this.tableHeader, ...this.tableData];
			this.requestSave();
			// TODO: Replace this timeout with the proper and recommended way.
			new Notice("Saving in progress...", 2005);
			debounce(() => {
			new Notice("File Saved !", 600);
			}, 2005);
		}
		clear() {
			this.tableData = [];
			if (this.div) this.div.empty();
			if (this.parent) this.parent.empty();
			this.contentEl.empty();
		}
		getViewType() {
		return VIEW_TYPE_CSV;
		}
	}

import {
		MarkdownPostProcessorContext,
		Notice,
		Plugin,
		WorkspaceLeaf,
		normalizePath,
		parseYaml,
	} from "obsidian";
	import { CSVView, VIEW_TYPE_CSV } from "./view";
	import processing from "./processing";
import { DEFAULT_SETTINGS } from "./defaults";
	import SettingsTab from "./SettingsTab";
	import ChartRenderer from "./ChartRenderer";
	export default class FinDocPlugin extends Plugin {
		settings: IPluginSettings;
		async loadSettings() {
			this.settings = Object.assign(
				{},
				DEFAULT_SETTINGS,
				await this.loadData()
		);
		}
		async saveSettings() {
			await this.saveData(this.settings);
		}
		async onload() {
			try {
				await this.loadSettings();
				this.addSettingTab(new SettingsTab(this.app, this));
				const { vault } = this.app;
			this.registerView(
					VIEW_TYPE_CSV,
					(leaf: WorkspaceLeaf) => new CSVView(leaf, this)
				);
				this.registerExtensions(["csv"], VIEW_TYPE_CSV);
				this.registerMarkdownCodeBlockProcessor(
					"findoc",
					async (
						src: string,
						el: HTMLElement,
					ctx: MarkdownPostProcessorContext
					) => {
						try {
							const activeFile = this.app.workspace.getActiveFile();
							if (!activeFile) {
								return;
							}
							const content = parseYaml(src);
							if (!content || !content.filename) {
								new Notice("No Content or No Filename", 10000);
							return;
							}
							if (content.filename) {
								const filename = normalizePath(
									`${activeFile.parent.path}/${content.filename}`
								);
								const data = await vault.adapter.read(filename);
								const chartData = processing(
									data,
									content.model,
								this.settings.models,
									this.settings.colors,
									this.settings.csvSeparator
								);
								if (chartData) {
									ctx.addChild(
										new ChartRenderer(
											this.settings.models[content.model],
											chartData,
											content.model,
										el
										)
									);
								} else {
									new Notice("Unable to generate chart", 10000);
								}
							}
						} catch (e) {
							new Notice(e.message, 10000);
							throw e;
					}
					}
				);
			} catch (e) {
				new Notice(e.message, 10000);
				throw e;
			}
		}
	}

export function getData(csv: string, separator = ","): Array<IInput> {
		return csv
			.split("\n")
			.filter(
				(line, idx) =>
					idx !== 0 && line !== "" && line.split(separator).length === 5
			)
			.map((line) => {
				return {
					type: line.split(separator)[0],
				id: line.split(separator)[1],
					value:
						parseFloat(line.split(separator)[2]) !== 0
							? parseFloat(line.split(separator)[2])
							: 0,
					timestamp: new Date(line.split(separator)[3]),
					extra: line.split(separator)[4],
				};
			})
			.sort((a, b) => {
			return (
					new Date(a.timestamp).getTime() -
					new Date(b.timestamp).getTime()
				);
			});
	}

/*
	 *	Functions to process the data
	 */
	import { getDate, getMonth, skipped } from "utils";
	export const functions: { [key: string]: any } = {
		splitByYear: (input: Array<IInput | any>) => {
			console.log(input);
			return input.reduce((acc, current) => {
				const d = new Date(current.timestamp);
				if (!acc[`${d.getUTCFullYear()}`]) {
				acc[`${d.getUTCFullYear()}`] = [];
				}
				acc[`${d.getUTCFullYear()}`].push(current);
				return acc;
			}, {});
		},
		splitByYearMonth: (input: Array<IInput | any>) => {
			return input.reduce((acc, current) => {
				const d = new Date(current.timestamp);
				if (!acc[`${d.getUTCFullYear()}-${getMonth(d)}`]) {
				acc[`${d.getUTCFullYear()}-${getMonth(d)}`] = [];
				}
				acc[`${d.getUTCFullYear()}-${getMonth(d)}`].push(current);
				return acc;
			}, {});
		},
		splitDailyDates: (input: Array<IInput | any>) => {
			return input.reduce((acc, current) => {
				const d = new Date(current.timestamp);
				const dateStr = `${d.getUTCFullYear()}-${getMonth(d)}-${getDate(
				d
				)}`;
				if (!acc[dateStr]) {
					acc[dateStr] = [];
				}
				acc[dateStr].push(current);
				return acc;
			}, {});
		},
		generateSumDataSet: ({
		typeToSelect,
			input,
			labels,
			types,
			colors,
		}: {
			typeToSelect: string[];
			input: { [key: string]: IInput[] };
			labels: string[];
			types: string[];
		colors: string[];
		}): IDataset => {
			const usableColors = [...colors];
			const datasets = types.map((type) => {
				const color = usableColors[0];
				usableColors.shift();
				console.log(color);
				return {
					label: type,
					borderColor: color,
				fill: false,
					tension: 0.2,
					spanGaps: true,
					segment: {
						borderColor: (ctx: IContext) => skipped(ctx, color),
						borderDash: (ctx: IContext) => skipped(ctx, [3, 3]),
					},
					data: labels
						.map((label: string) => {
							return input[label]
							.filter((i: IInput) =>
									typeToSelect.includes(i.type)
								)
								.reduce(
									(
										types: { [key: string]: number },
										current: IInput
									) => {
										if (!types[current.id])
											types[current.id] = 0;
									types[current.id] += current.value;
										return types;
									},
									{}
								);
						})
						.reduce((typeSum, current) => {
							if (current[type]) typeSum.push(current[type]);
							else typeSum.push(0);
							return typeSum;
					}, []),
				};
			});
			return {
				labels,
				datasets,
			};
		},
		generateDailyDataSet: ({
			typeToSelect,
		input,
			labels,
			types,
			colors,
		}: {
			typeToSelect: string[];
			input: { [key: string]: IInput[] };
			labels: string[];
			types: string[];
			colors: string[];
	}): IDataset => {
			const nonEmptyLabels: string[] = [];
			const usableColors = [...colors];
			const datasets = types.map((type) => {
				const color = usableColors[0];
				usableColors.shift();
				return {
					label: type,
					borderColor: color,
					fill: false,
				tension: 0.2,
					spanGaps: true,
					segment: {
						borderColor: (ctx: IContext) => skipped(ctx, color),
						borderDash: (ctx: IContext) => skipped(ctx, [3, 3]),
					},
					data: labels
						.map((label: string) => {
							const typesFound = input[label]
								.filter((i: IInput) =>
								typeToSelect.includes(i.type)
								)
								.reduce(
									(
										types: { [key: string]: number },
										current: IInput
									) => {
										if (!types[current.id])
											types[current.id] = 0;
										types[current.id] = current.value;
									return types;
									},
									{}
								);
							if (
								Object.keys(typesFound).length > 0 &&
								!nonEmptyLabels.includes(label)
							)
								nonEmptyLabels.push(label);
							return typesFound;
					})
						.filter((current) => {
							let total = 0;
							Object.values(current).forEach((c: number) => {
								total += c;
							});
							if (total === 0) return null;
							return current;
						})
						.reduce((typeSum, current) => {
						if (current[type]) typeSum.push(current[type]);
							else typeSum.push(NaN);
							return typeSum;
						}, []),
				};
			});
			return {
				labels: nonEmptyLabels,
				datasets,
			};
	},
		generateSumDataSetPerTypes: ({
			typeToSelect,
			input,
			labels,
			colors,
		}: {
			typeToSelect: string[];
			input: { [key: string]: IInput[] };
			labels: string[];
		colors: string[];
		}): IDataset => {
			const usableColors = [...colors];
			const datasets = typeToSelect.map((type) => {
				const color = usableColors[0];
				usableColors.shift();
				console.log(color);
				return {
					label: type,
					borderColor: color,
				fill: false,
					tension: 0.2,
					spanGaps: true,
					segment: {
						borderColor: (ctx: IContext) => skipped(ctx, color),
						borderDash: (ctx: IContext) => skipped(ctx, [3, 3]),
					},
					data: Object.values(input).map((i) => {
						return i
							.filter((entry) => entry.type === type)
						.reduce((acc, current) => {
								acc += current.value;
								return acc;
							}, 0);
					}),
				};
			});
			return {
				labels,
				datasets,
		};
		},
	};

