/* Date */
	export function getMonth(d: Date): string | number {
		if (d.getUTCMonth() + 1 < 10) return "0" + (d.getUTCMonth() + 1);
		return d.getUTCMonth() + 1;
	}
	export function getDate(d: Date): string | number {
