private void clearInputData() {
    var emptyText = "";
    txtPersonName.setText(emptyText);
    txtPersonDob.setText(emptyText);
    txtPersonAddress.setText(emptyText);
    txtPersonId.setText(emptyText);
    txtPersonEmail.setText(emptyText);
