package com.utils;

public class ObjectAndType
{
	Object object;
	Class type;

	public ObjectAndType(Object object, Class type)
	{
		this.object = object;
		this.type = type;
	}
}

public int getId()
{
	return groupId << 16 | childId;
}

/**
 * Gets the group ID of the pair.
 *
 * @return the group ID
 */
public int getGroupId()
{
	return groupId;
}

/**
 * Gets the ID of the child in the group.
 *
 * @return the child ID
 */

public int getGroupId()
{
	return groupId;
}

/**
 * Gets the ID of the child in the group.
 *
 * @return the child ID
 */
public int getChildId()
{
	return childId;
}

/**
 * Gets the packed widget ID.
 *
 * @return the packed ID
 */

public int getChildId()
{
	return childId;
}

/**
 * Gets the packed widget ID.
 *
 * @return the packed ID
 */
public int getPackedId()
{
	return groupId << 16 | childId;
}

/**
 * Utility method that converts an ID returned by {@link #getId()} back
 * to its group ID.
 *
 * @param id passed group-child ID
 * @return the group ID
 */

public int getPackedId()
{
	return groupId << 16 | childId;
}

/**
 * Utility method that converts an ID returned by {@link #getId()} back
 * to its group ID.
 *
 * @param id passed group-child ID
 * @return the group ID
 */

public static int TO_GROUP(int id)
{
	return id >>> 16;
}

/**
 * Utility method that converts an ID returned by {@link #getId()} back
 * to its child ID.
 *
 * @param id passed group-child ID
 * @return the child ID
 */

public static int TO_CHILD(int id)
{
	return id & 0xFFFF;
}

/**
 * Packs the group and child IDs into a single integer.
 *
 * @param groupId the group ID
 * @param childId the child ID
 * @return the packed ID
 */

	public static int PACK(int groupId, int childId)
	{
		return groupId << 16 | childId;
	}

}

package com.utils;

import net.runelite.client.config.Config;
import net.runelite.client.config.ConfigGroup;
