		client.getCanvas().dispatchEvent(keyRelease);
		KeyEvent keyTyped = new KeyEvent(client.getCanvas(), KeyEvent.KEY_TYPED, System.currentTimeMillis(), 0, KeyEvent.VK_BACK_SPACE);
		client.getCanvas().dispatchEvent(keyTyped);
	}
