def parse_args(args):
    parser = argparse.ArgumentParser()
    parser.add_argument("--config_file", type=str, help="", default=None)
    return parser.parse_args(args)

def init_job(args):
    Configs = namedtuple("Configs", "job_config context")
    job_args = parse_args(args)
    job_config = override_config(job_args.config_file, inference=True)

    torch.manual_seed(job_config.seed)
    torch.cuda.manual_seed(job_config.seed)
    torch.backends.cuda.matmul.allow_tf32 = True  # allow tf32 on matmul
    torch.backends.cudnn.allow_tf32 = True  # allow tf32 on cudnn
    ptdtype = {
        "float32": torch.float32,
        "bfloat16": torch.bfloat16,
        "float16": torch.float16,
    }[job_config.dtype]
    ctx = (
        nullcontext()
        if job_config.device_type == "cpu"
        else torch.amp.autocast(device_type=job_config.device_type, dtype=ptdtype)
    )

    return Configs(job_config, ctx)

class InferenceModelInitialiser(ModelInitialiser):
    def __init__(self, configs, **kwargs):
        super(InferenceModelInitialiser, self).__init__(configs, **kwargs)

    def init_model(self):
        InitialisedModel = namedtuple("InitialisedModel", "model checkpoint")
        # model
        checkpoint = None
        if self.configs.job_config.init_from == "resume":
            # init from a model saved in a specific directory
            model, checkpoint = self.init_resume(training_args=False)
        elif self.configs.job_config.init_from.startswith("gpt2"):
            # init from a given GPT-2 model
            model = GPT.from_pretrained(
                self.configs.job_config.init_from, dict(dropout=0.0)
            )

        return InitialisedModel(model, checkpoint)

