class Ui_DoubleSlider:
    class Value:
        def __init__(self):
            self.smallVal = 1
            self.bigVal = 0

        def getValue(self):
            return self.smallVal + self.bigVal

    def __init__(self, description, effectFactory):
        self.v = self.Value()
        self.description = description
        self.effectFactory = effectFactory
