def extract_score(sentence):
    number = re.search("^\d+\.\d+", sentence)
    if number is None:
        number = re.search("^\d+", sentence)
    if number is None:
        return 0, 0
    score = float(number.group(0))
    if score < 0 or score > 10:
        print(sentence)
        print(score)
        raise Exception
    return score, 1

