def load_general_prompts(path):
    dataset = []
    with open(path, "r") as f:
        while True:
            line = f.readline()
            if line == "":
                break
            elif line == "\n":
                continue
            assert line.startswith("PROMPT: ")
            prompt = line[len("PROMPT: ") :].strip()

            entry = {"prompt": prompt, "questions": [], "answers": []}

            for key in ["sub", "verb", "obj"]:
                line = f.readline()
                assert line.startswith(f"{key.upper()} Q: ")
                entry["questions"].append(line[len(f"{key.upper()} Q: ") :].strip())
                line = f.readline()
                assert line.startswith(f"{key.upper()} A: ")
                entry["answers"].append(line[len(f"{key.upper()} A: ") :].strip())

            dataset.append(entry)
    return dataset

def watch(args_to_watch):
    def _fn(args):
        exp_name = []
        for key, label in args_to_watch:
            if not hasattr(args, key):
                continue
            val = getattr(args, key)
            if type(val) == dict:
                val = "_".join(f"{k}-{v}" for k, v in val.items())
            exp_name.append(f"{label}{val}")
        exp_name = "_".join(exp_name)
        exp_name = exp_name.replace("/_", "/")
        exp_name = exp_name.replace("(", "").replace(")", "")
        exp_name = exp_name.replace(", ", "-")
        return exp_name

    return _fn

def lazy_fstring(template, args):
    ## https://stackoverflow.com/a/53671539
    return eval(f"f'{template}'")


class LazyFn:
    def __init__(self, fn):
        self.fn = fn

    def __call__(self, args):
