	GPT3516k = "gpt-3.5-turbo-16k",
	GPT4 = "gpt-4",
	Davinci = "text-davinci-003",
}

export interface GPTSettings {
	maxTokens: number
	temperature: number
	topP: number
	presencePenalty: number
	frequencyPenalty: number
	stop: string[]
}

